CREATE OR REPLACE FUNCTION obter_fator_rh_abo ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_fator_rh_w		varchar(1);
ie_tipo_sangue_w	varchar(2);

BEGIN

SELECT	max(ie_fator_rh),
	max(ie_tipo_sangue)
into STRICT	ie_fator_rh_w,
	ie_tipo_sangue_w
FROM   	pessoa_fisica
WHERE  	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_opcao_p = 'RH') then
	return ie_fator_rh_w;
else
	return ie_tipo_sangue_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
