CREATE OR REPLACE FUNCTION obter_feriado ( dt_feriado_p timestamp, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_motivo_feriado_w	varchar(40);


BEGIN

begin
select	ds_motivo_feriado
into STRICT	ds_motivo_feriado_w
from	feriado
where	dt_feriado		= dt_feriado_p
and	cd_estabelecimento	= cd_estabelecimento_p;
exception
	 when others then
	 ds_motivo_feriado_w	:= null;
end;

return	ds_motivo_feriado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
