CREATE OR REPLACE FUNCTION obter_fila_agenda_horario (cd_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


cd_fila_w	integer;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then
	SELECT MAX(a.NR_SEQ_FILA_ESPERA) into STRICT cd_fila_w
    FROM AGENDA_HORARIO a
	WHERE a.CD_AGENDA = CD_AGENDA_P AND a.dt_dia_semana = 9 OR (a.ie_dia_semana = obter_cod_dia_semana(clock_timestamp())
	OR (a.ie_dia_semana = 0 AND Obter_Se_Feriado(obter_estabelecimento_ativo, clock_timestamp()) > 0))
	and clock_timestamp() between a.HR_INICIAL and a.HR_FINAL;
end if;

return cd_fila_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
