CREATE OR REPLACE FUNCTION obter_fila_padrao_senha (cd_perfil_ativo_p bigint, nr_seq_local_chamada_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(10);


BEGIN

select 	coalesce(max(b.nr_seq_fila_espera),'')
into STRICT		ds_retorno_w
from 	filas_liberadas_transf a,
			filas_lib_transf_item b
where 	a.nr_sequencia = b.nr_seq_regra_filas_lib
and 		b.ie_padrao = 'S'
and 		a.cd_perfil = cd_perfil_ativo_p
and		((a.nr_seq_local_chamada = nr_seq_local_chamada_p) or (coalesce(a.nr_seq_local_chamada::text, '') = ''));

return ds_retorno_w;

end;
