CREATE OR REPLACE FUNCTION obter_fila_regra_transf (cd_perfil_p bigint, nr_seq_local_senha_p bigint, nr_seq_fila_p bigint) RETURNS varchar AS $body$
DECLARE


ie_fila_liberada_w	varchar(1);
nr_sequencia_w		bigint;
qt_regra_local_w	bigint;
ie_achou_regra_w	varchar(1);
ie_fila_w		varchar(1);

C01 CURSOR FOR
	SELECT	ie_permite_transf,
		nr_sequencia
	from	filas_liberadas_transf a
	where	a.cd_perfil = cd_perfil_p
	and	coalesce(a.nr_seq_local_chamada,nr_seq_local_senha_p) = nr_seq_local_senha_p
	and	a.ie_situacao = 'A'
	order by nr_seq_local_chamada;

BEGIN

ie_achou_regra_w := 'N';
ie_fila_w := 'N';

open C01;
loop
fetch C01 into
	ie_fila_liberada_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_achou_regra_w = 'N') then

		ie_achou_regra_w := 'S';

		select	count(*)
		into STRICT	qt_regra_local_w
		from 	filas_lib_transf_item b
		where	nr_seq_regra_filas_lib = nr_sequencia_w
		and	coalesce(b.nr_seq_fila_espera,nr_seq_fila_p) = nr_seq_fila_p;

		ie_fila_w := ie_fila_liberada_w;

		if (ie_fila_liberada_w = 'S') and (qt_regra_local_w = 0) then
			ie_fila_w := 'N';
		elsif (ie_fila_liberada_w = 'N') and (qt_regra_local_w = 0) then
			ie_fila_w := 'S';
		end if;

	end if;

	end;
end loop;
close C01;

return	ie_fila_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
