CREATE OR REPLACE FUNCTION obter_filiacao ( CD_PESSOA_FISICA_P text, IE_NOME_P text) RETURNS varchar AS $body$
DECLARE


ds_nome_w		varchar(80) := '';


BEGIN
if (IE_NOME_P	= 'M') then
	begin
	select max(m.nm_contato)
	into STRICT	ds_nome_w
	FROM compl_pessoa_fisica m, pessoa_fisica f
WHERE f.cd_pessoa_fisica	= m.cd_pessoa_fisica  and f.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end;
elsif (IE_NOME_P	= 'P') then
	begin
	select max(p.nm_contato)
	into STRICT	ds_nome_w
	FROM compl_pessoa_fisica p, pessoa_fisica f
WHERE f.cd_pessoa_fisica	= p.cd_pessoa_fisica  and f.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end;
elsif (IE_NOME_P	= 'C') then
	begin
	select max(p.nm_contato)
	into STRICT	ds_nome_w
	FROM compl_pessoa_fisica p, pessoa_fisica f
WHERE f.cd_pessoa_fisica	= p.cd_pessoa_fisica  and f.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end;
end if;

RETURN ds_nome_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
