SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_filtro_itens_presc_farm (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
Qt_registro_medic_w	bigint	:= 0;
Qt_registro_mat_w	bigint	:= 0;
Qt_registro_SNE_w	bigint	:= 0;
Qt_registro_supl_w	bigint	:= 0;
ds_result_w			varchar(1) := null;
ie_agrupador_w		smallint;
dt_inicio_anal_farm_w	timestamp;


BEGIN

/*
ESTA FUNCTION FAZ O SEGUINTE TRATAMENTO - OS 287406 ( A pedido do HSL)
Exibir prescrições que estão com a anáilise iniciada.
Exibir todas prescrições que tenham itens
Não exibir prescrições que possuam todos os itens suspensos
Exibir todas as prescrições que não tenha nenhum item
*/
select	max(dt_inicio_analise_farm)
into STRICT	dt_inicio_anal_farm_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (dt_inicio_anal_farm_w IS NOT NULL AND dt_inicio_anal_farm_w::text <> '') then
	Ds_result_w	:= 'S';
end if;

if (coalesce(Ds_result_w::text, '') = '') then
	select	count(*)
	into STRICT	Qt_registro_w
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(nr_seq_origem::text, '') = ''
	and	coalesce(nr_seq_exame::text, '') = ''
	and 	((Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced,'O') = 'S') or (Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced,'IVC') = 'S'))
	and	coalesce(ie_suspenso, 'N') 	<> 'S';

	if (Qt_registro_w <> 0) then
		Ds_result_w	:= 	'S';
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_procedimento
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(nr_seq_origem::text, '') = ''
		and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
		and 	((Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced,'O') = 'S') or (Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced,'IVC') = 'S'))
		and	coalesce(ie_suspenso, 'N') 	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT 	qt_registro_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	ie_agrupador in (1,2,8,12)
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (qt_registro_w > 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		and	coalesce(ie_hemodialise,'N')	= 'N';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		and	coalesce(ie_hemodialise,'N')	= 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		and	coalesce(ie_hemodialise,'N')	= 'P';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_dieta
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	rep_jejum
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_recomendacao
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		Select sum(ct)
		into STRICT	Qt_registro_w
		from (	SELECT	count(*) ct

			from	NUT_PACIENTE
			where	nr_prescricao	= nr_prescricao_p
			and	coalesce(ie_suspenso, 'N')	<> 'S'
			
union all

			SELECT	count(*)
			from	NUT_PAC
			where	nr_prescricao	= nr_prescricao_p
			and 	coalesce(ie_npt_adulta,'N')  = 'S'
			and	coalesce(ie_suspenso, 'N')	<> 'S'
		) alias7;

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	NUT_PAC
		where	nr_prescricao	= nr_prescricao_p
		and 	coalesce(ie_npt_adulta,'N') not in ('S', 'P')
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	NUT_PAC
		where	nr_prescricao	= nr_prescricao_p
		and 	coalesce(ie_npt_adulta,'N') = 'P'
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao	= nr_prescricao_p;

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	qt_registro_w
		from	prescr_leite_deriv
		where	nr_prescricao = nr_prescricao_p;

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_gasoterapia
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (Qt_registro_w = 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_opme
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S';

		if (Qt_registro_w <> 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;

	if (substr(Ds_result_w,1,1) = ' ') then
		Ds_result_w	:= 'S';
	end if;

	--Exibir todas as prescrições que não tenha nenhum item;
	if (coalesce(Ds_result_w::text, '') = '') then
		SELECT	sum(qt_registro)
		into STRICT	qt_registro_w
		FROM (
			SELECT COUNT(*) qt_registro
			FROM   prescr_procedimento
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_solucao
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_dieta
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   rep_jejum
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_recomendacao
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   NUT_PACIENTE
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   NUT_PAC
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_solic_bco_sangue
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_leite_deriv
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_gasoterapia
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_opme
			WHERE  nr_prescricao = nr_prescricao_p
			
UNION

			SELECT COUNT(*) qt_registro
			FROM   prescr_material
			WHERE  nr_prescricao = nr_prescricao_p) alias15;

		if (qt_registro_w = 0) then
			Ds_result_w	:= 'S';
		end if;
	end if;
end if;

return Ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_filtro_itens_presc_farm (nr_prescricao_p bigint) FROM PUBLIC;

