CREATE OR REPLACE FUNCTION obter_fim_horario_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horario_w		varchar(15);
ds_resultado_w		varchar(2000);
ds_horario_fim_w	varchar(2000);

C01 CURSOR FOR
SELECT	to_char(b.dt_fim_horario,'hh24:mi'),
	to_char(DT_HORARIO,'hh24:mi')
from	prescr_mat_hor b,
	prescr_medica a
where	a.nr_prescricao		= b.nr_prescricao
and	a.nr_prescricao		= nr_prescricao_p
and	b.nr_seq_material	= nr_sequencia_p
and	coalesce(b.dt_suspensao::text, '') = ''
and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S'
order by b.dt_horario;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_horario_fim_w,
	ds_horario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_opcao_p = 0 ) then
		ds_resultado_w	:= ds_horario_fim_w ||'-'||ds_horario_w;
	elsif (ie_opcao_p = 1) then
		ds_resultado_w	:= ds_resultado_w ||'-'||ds_horario_w;
	end if;
	end;
END LOOP;
CLOSE C01;

ds_resultado_w	:= substr(ds_resultado_w,2,length(ds_resultado_w));

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
