CREATE OR REPLACE FUNCTION obter_fim_jejum_hoje (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_fim_w	timestamp;

BEGIN

select	max(a.dt_fim)
into STRICT	dt_fim_w
from	rep_jejum a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	coalesce(b.dt_suspensao::text, '') = ''
and	coalesce(a.ie_suspenso,'N') <> 'S'
and	dt_fim > clock_timestamp()
and	b.nr_atendimento = nr_atendimento_p;

return	dt_fim_w;

end;
$body$
