CREATE OR REPLACE FUNCTION obter_fluxo_caixa_lote_regra ( nr_seq_regra_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
VL - Valor de dedução		- Retorna o Valor
E - Se exclui ou não a regra	- Retorna "S" ou "N" */
ds_retorno_w			varchar(255);
ie_acao_w			varchar(255);
ie_regra_exclusao_w		varchar(100);
ie_origem_tit_pagar_w		varchar(100);
ie_origem_tit_rec_w		varchar(100);
cd_pessoa_fisica_w		varchar(100);
ie_regra_data_w			varchar(100);
cd_cgc_w			varchar(100);
ie_somente_dia_util_w		varchar(1);
vl_minimo_w			double precision;
vl_maximo_w			double precision;
vl_saldo_total_w		double precision;
vl_saldo_titulo_w		double precision;
nr_seq_classe_tit_pagar_w	bigint;
qt_dia_inicial_w		bigint;
qt_dia_final_w			bigint;
cont_w				bigint;
dt_referencia_w			timestamp;
vl_fixo_w			double precision;
ie_regra_origem_w		varchar(3);
cd_conta_financ_w		bigint;
dt_inicial_w			timestamp;
dt_final_w			timestamp;

c01 CURSOR FOR
SELECT	ie_regra_exclusao,
	ie_origem_tit_pagar,
	ie_origem_tit_rec,
	cd_pessoa_fisica,
	cd_cgc,
	qt_dia_inicial,
	qt_dia_final,
	ie_regra_data,
	vl_minimo,
	vl_maximo,
	ie_acao,
	nr_seq_classe_tit_pagar,
	ie_somente_dia_util
from	regra_fluxo_caixa_exc
where	nr_seq_regra		= nr_seq_regra_p;


BEGIN

ds_retorno_w		:= 'S';
vl_saldo_total_w	:= 0;

select	max(a.vl_fixo),
	max(a.ie_regra_origem),
	max(a.cd_conta_financ)
into STRICT	vl_fixo_w,
	ie_regra_origem_w,
	cd_conta_financ_w
from	regra_fluxo_caixa a
where	a.nr_sequencia	= nr_seq_regra_p;

if (ie_somente_dia_util_w = 'S') then
	dt_referencia_w	:= obter_proximo_dia_util(cd_estabelecimento_p, dt_referencia_p);
else
	dt_referencia_w	:= dt_referencia_p;
end if;

open C01;
loop
fetch C01 into
	ie_regra_exclusao_w,
	ie_origem_tit_pagar_w,
	ie_origem_tit_rec_w,
	cd_pessoa_fisica_w,
	cd_cgc_w,
	qt_dia_inicial_w,
	qt_dia_final_w,
	ie_regra_data_w,
	vl_minimo_w,
	vl_maximo_w,
	ie_acao_w,
	nr_seq_classe_tit_pagar_w,
	ie_somente_dia_util_w;
EXIT WHEN NOT FOUND or ds_retorno_w = 'N';  /* apply on c01 */

	dt_inicial_w := obter_data_valida(qt_dia_inicial_w, dt_referencia_w);
	dt_final_w := obter_data_valida(qt_dia_final_w, dt_referencia_w);

	if (ie_regra_exclusao_w = 'TP') then

		select	count(*),
			coalesce(sum(vl_saldo_titulo),0)
		into STRICT	cont_w,
			vl_saldo_titulo_w
		from	titulo_pagar
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	ie_origem_titulo		= coalesce(ie_origem_tit_pagar_w, ie_origem_titulo)
		and	coalesce(cd_pessoa_fisica, 'X')	= coalesce(cd_pessoa_fisica_w, coalesce(cd_pessoa_fisica, 'X'))
		and	coalesce(cd_cgc, 'X') 		= coalesce(cd_cgc_w, coalesce(cd_cgc, 'X'))
		and	vl_titulo			between coalesce(vl_minimo_w, vl_titulo) and coalesce(vl_maximo_w, vl_titulo)
		and	coalesce(nr_seq_classe,0)	= coalesce(nr_seq_classe_tit_pagar_w, coalesce(nr_seq_classe, 0))
		and	exists (SELECT 	1
				from 	titulo_pagar_classif x
				where 	x.nr_titulo = nr_titulo
				and	x.nr_seq_conta_financ = cd_conta_financ_w)
		and	((ie_regra_data_w	= 'E' AND dt_emissao between dt_inicial_w and dt_final_w)
			or (ie_regra_data_w	= 'V' AND dt_vencimento_atual	between dt_inicial_w and dt_final_w)
			);

	elsif (ie_regra_exclusao_w = 'TR') then

		select	count(*),
			coalesce(sum(vl_saldo_titulo),0)
		into STRICT	cont_w,
			vl_saldo_titulo_w
		from	titulo_receber
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	ie_origem_titulo	= coalesce(ie_origem_tit_rec_w, ie_origem_titulo)
		and	coalesce(cd_pessoa_fisica, 'X') = coalesce(cd_pessoa_fisica_w, coalesce(cd_pessoa_fisica, 'X'))
		and	coalesce(cd_cgc, 'X') 	= coalesce(cd_cgc_w, coalesce(cd_cgc, 'X'))
		and	vl_titulo		between coalesce(vl_minimo_w, vl_titulo) and coalesce(vl_maximo_w, vl_titulo)
		and	exists (SELECT 	1
				from 	titulo_receber_classif x
				where 	x.nr_titulo = nr_titulo
				and	x.cd_conta_financ = cd_conta_financ_w)
		and	((ie_regra_data_w	= 'E' AND dt_emissao between dt_inicial_w and dt_final_w)
			or (ie_regra_data_w	= 'V' AND dt_pagamento_previsto between dt_inicial_w and dt_final_w)
			);

	end if;

	vl_saldo_total_w	:= vl_saldo_total_w + vl_saldo_titulo_w;

	if (ie_acao_w		= 'M') and (ie_regra_origem_w	= 'VF') and (vl_saldo_total_w	> vl_fixo_w) then

		vl_saldo_total_w	:= vl_fixo_w;

	end if;

	if (ie_opcao_p = 'E') and (ie_acao_w = 'E') then
		ds_retorno_w		:= 'S';
		if (cont_w > 0) then
			ds_retorno_w	:= 'N';
		end if;
	end if;
end loop;
close C01;

if (ie_opcao_p = 'VL') then
	ds_retorno_w	:= vl_saldo_total_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_fluxo_caixa_lote_regra ( nr_seq_regra_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint, ie_opcao_p text) FROM PUBLIC;

