CREATE OR REPLACE FUNCTION obter_fluxo_manchester_cli ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_fluxo_w	bigint;


BEGIN
	ds_retorno_w := '';

	Select 	max(NR_SEQ_FLUXO)
	into STRICT	nr_seq_fluxo_w
	from	MANCHESTER_FLUXOGRAMA_CLI
	where	nr_sequencia =  nr_sequencia_p;

	if (coalesce(nr_seq_fluxo_w,0) > 0) then

		SELECT substr(NM_FLUXOGRAMA, 1, 255)
		INTO STRICT   ds_retorno_w
		FROM   MANCHESTER_FLUXOGRAMA a
		WHERE  a.NR_SEQUENCIA = nr_seq_fluxo_w;

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
