SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_formato_data ( data_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);			
qt_seg_w	bigint;
qt_min_w	bigint;
qt_hora_w	bigint;
			

BEGIN

if (data_p IS NOT NULL AND data_p::text <> '')	and (ie_opcao_p = 'S') 	then  /*Utilizado para diferenca na subtracao de datas recebendo como parametro o fromato do oracle ex.: 183,827731481481481481481481481481481482*/
	qt_seg_w	:= trunc((data_p) * 86400);		/*verifica quantos segundos totais existem*/
	qt_hora_w	:= trunc(qt_seg_w / 3600);		/*verifica a quantidade de horas a partir dos segundos totais*/
	qt_seg_w	:= qt_seg_w - (qt_hora_w * 3600);	/*quantidade de segundos que sobram*/
	qt_min_w	:= trunc(qt_seg_w / 60);		/*quantidade de minutos a partir dos segundos que sobraram*/
	qt_seg_w	:= qt_seg_w - (qt_min_w * 60);		/*quantidade de segundos que sobram tirando os minutos*/
	ds_retorno_w	:= to_char(qt_hora_w,'FM999900') || ':' || to_char(qt_min_w,'FM00') || ':' || to_char(qt_seg_w,'FM00');

end if;	
	
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
