CREATE OR REPLACE FUNCTION obter_formato_interface ( ds_valor_p text, ie_formato_p text ) RETURNS varchar AS $body$
DECLARE

    ds_resultado  varchar(2000);
    nr_tam        bigint;

BEGIN
    ds_resultado := ds_valor_p;
    IF (ie_formato_p IS NOT NULL AND ie_formato_p::text <> '') THEN
        IF ie_formato_p = '1' THEN
            ds_resultado := ds_valor_p || '00';
        ELSIF ie_formato_p = '2' THEN
            ds_resultado := '000'
                            || ds_valor_p
                            || '00';
        ELSIF ie_formato_p = '3' THEN
            nr_tam := length(ds_resultado);
            ds_resultado := lpad(('0'
                                           || substr(ds_resultado, 1, nr_tam - 3))::numeric , 6, '0')
                            || rpad(('0'
                                              || substr(ds_resultado, nr_tam - 2))::numeric , 3, '0');

        END IF;

        ds_resultado := regexp_replace(ds_resultado, '[^0-9]', '');
    END IF;

    RETURN ds_resultado;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
