CREATE OR REPLACE FUNCTION obter_formato_result_exame (nr_seq_exame_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_formato_w	varchar(3);


BEGIN

select coalesce(max(ie_formato_resultado),'XXX')
into STRICT ie_formato_w
from exame_lab_material
where nr_seq_exame = nr_seq_exame_p
  and nr_seq_material = nr_seq_material_p;

if (ie_formato_w = 'XXX') then
	select coalesce(max(ie_formato_resultado),'')
	into STRICT ie_formato_w
	from exame_laboratorio
	where nr_seq_exame = nr_seq_exame_p;
end if;

return ie_formato_w;

end;
$body$
