CREATE OR REPLACE FUNCTION obter_forma_armazenamento ( nr_seq_registro_p bigint, nr_seq_marca_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


nr_forma_armazenamento_w	material_armazenamento.nr_seq_forma%type;
ds_forma_armaz_w			mat_forma_armaz.ds_forma_armaz%type;
cd_material_w				inspecao_recebimento.cd_material%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;




BEGIN

cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1);

if (ie_tipo_p = 'I') then

	select  cd_material
	into STRICT	cd_material_w
	from	inspecao_recebimento
	where 	nr_sequencia = nr_seq_registro_p;

elsif (ie_tipo_p  = 'C') then

	select  cd_material
	into STRICT	cd_material_w
	from	inspecao_contagem
	where 	nr_sequencia = nr_seq_registro_p;

end if;

begin
select	coalesce(max(nr_seq_forma),0)
into STRICT	nr_forma_armazenamento_w
from	material_armazenamento
where	cd_material = cd_material_w
and	coalesce(nr_seq_marca,0) = coalesce(nr_seq_marca_p,0)
and 	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;
exception
when others then
	nr_forma_armazenamento_w := 0;
end;

select 	ds_forma_armaz
into STRICT	ds_forma_armaz_w
from	mat_forma_armaz
where	nr_sequencia = nr_forma_armazenamento_w;

return	ds_forma_armaz_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
