CREATE OR REPLACE FUNCTION obter_forma_compra_ordem ( nr_ordem_compra_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_forma_compra_w		bigint;			
			

BEGIN

select	max(a.nr_seq_forma_compra)
into STRICT	nr_seq_forma_compra_w
from	solic_compra a,
	(SELECT	distinct
	nr_solic_compra,
	nr_cot_compra
from (
	SELECT	distinct x.nr_cot_compra,
		x.nr_solic_compra
	from	cot_compra_item x,
  ordem_compra_item z
  where x.nr_cot_compra = z.nr_cot_compra
  and z.nr_ordem_compra = nr_ordem_compra_p
	
union all

	select	distinct x.nr_cot_compra ,
		x.nr_solic_compra
	from	cot_compra_solic_agrup x,
  ordem_compra_item z
  where x.nr_cot_compra = z.nr_cot_compra
  and z.nr_ordem_compra = nr_ordem_compra_p
  ) alias1) b,
	ordem_compra_item c
where	a.nr_solic_compra = b.nr_solic_compra
and	c.nr_cot_compra = b.nr_cot_compra
and	c.nr_ordem_compra = nr_ordem_compra_p
;

return	nr_seq_forma_compra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
