CREATE OR REPLACE FUNCTION obter_forma_entrega_regra ( nr_seq_proc_interno_p bigint, cd_medico_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			ageint_regra_entrega_exame.nr_seq_forma_laudo%type;

cd_area_proced_w		especialidade_proc.cd_area_procedimento%type;
cd_espec_proced_w		grupo_proc.cd_especialidade%type;
cd_grupo_proced_w		procedimento.cd_grupo_proc%type;

nr_seq_forma_laudo_w	ageint_regra_entrega_exame.nr_seq_forma_laudo%type;
	
C01 CURSOR FOR
	SELECT	nr_seq_forma_laudo
	from	ageint_regra_entrega_exame
	where	((cd_area_procedimento	= cd_area_proced_w)	or (coalesce(cd_area_procedimento::text, '') = ''))
	and	((cd_especialidade	= cd_espec_proced_w)	or (coalesce(cd_especialidade::text, '') = ''))
	and	((cd_grupo_proc		= cd_grupo_proced_w)	or (coalesce(cd_grupo_proc::text, '') = ''))
	and	((cd_procedimento	= cd_procedimento_p)	or (coalesce(cd_procedimento::text, '') = ''))
	and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_p) or (coalesce(ie_origem_proced::text, '') = '')))
	and	((nr_seq_proc_interno	= nr_seq_proc_interno_p)or (coalesce(nr_seq_proc_interno::text, '') = ''))
	and	((cd_pessoa_Fisica	= cd_medico_p)		or (coalesce(cd_pessoa_Fisica::text, '') = ''))
	order by coalesce(cd_procedimento,0),
		coalesce(nr_seq_proc_interno,0),
		coalesce(cd_area_procedimento,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_pessoa_Fisica, 0);
	

BEGIN

select	coalesce(max(cd_area_procedimento),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_grupo_proc),0)
into STRICT	cd_area_proced_w,
	cd_espec_proced_w,
	cd_grupo_proced_w
from	estrutura_procedimento_v
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;

open C01;
loop
fetch C01 into	
	nr_seq_forma_laudo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	vl_retorno_w	:= nr_seq_forma_laudo_w;
	end;
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
