CREATE OR REPLACE FUNCTION obter_forma_exportar_solic ( nr_solic_compra_p bigint, ie_forma_exportar_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1);
qt_existe_w		integer;

/*	ie_opcao_p:
S = Solicitacao
I - Item da solicitação	*/
BEGIN

ds_retorno_w	:= 'N';

select	count(*)
into STRICT	qt_existe_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra
and	ie_forma_exportar = ie_forma_exportar;

if (qt_existe_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return coalesce(ds_retorno_w, 'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
