CREATE OR REPLACE FUNCTION obter_forma_pagto_cartao_cx (nr_seq_caixa_receb_p bigint, nr_seq_forma_pagto_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_caixa_w	bigint;
ds_retorno_w	varchar(1) := 'S';			
qt_registro_w	bigint;

BEGIN

select	max(b.nr_seq_caixa)
into STRICT	nr_seq_caixa_w
from	caixa_receb a,
	caixa_saldo_diario b
where	a.nr_seq_saldo_caixa = b.nr_sequencia
and	a.nr_sequencia = nr_seq_caixa_receb_p;

if (nr_seq_caixa_w IS NOT NULL AND nr_seq_caixa_w::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	forma_pagto_caixa
	where	nr_seq_caixa = nr_seq_caixa_w
	and		ie_situacao	= 'A';
	
	if (qt_registro_w > 0) then
		select count(*)
		into STRICT	qt_registro_w
		from	forma_pagto_caixa
		where	nr_seq_forma_pagto = nr_seq_forma_pagto_p
		and	nr_seq_caixa = nr_seq_caixa_w
		and 	ie_situacao = 'A';
		
		if (qt_registro_w > 0) then
			ds_retorno_w := 'S';
		else
			ds_retorno_w := 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
