CREATE OR REPLACE FUNCTION obter_forma_rateio_sadt_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_convenio_parametro_w		integer;
cd_estabelecimento_w		smallint;
ie_forma_rateio_sadt_w		varchar(10);


BEGIN 
 
begin 
select	cd_convenio_parametro, 
	cd_estabelecimento 
into STRICT	cd_convenio_parametro_w, 
	cd_estabelecimento_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
exception 
  	when others then 
   	cd_convenio_parametro_w := 0;
	cd_estabelecimento_w	:= 0;
end;
 
select	coalesce(max(ie_forma_rateio_sadt),'P') 
into STRICT	ie_forma_rateio_sadt_w 
from	convenio_estabelecimento 
where	cd_convenio		= cd_convenio_parametro_w 
and	cd_estabelecimento	= cd_estabelecimento_w;
 
return	ie_forma_rateio_sadt_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
