CREATE OR REPLACE FUNCTION obter_forn_maior_venc ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_fornec_w			bigint;
nr_seq_fornec_result_w		bigint;
qt_contador_w			bigint := 0;
qt_contador_ww			bigint := 0;

c01 CURSOR FOR 
SELECT	distinct 
	a.nr_sequencia 
from	cot_compra_forn a 
where	a.nr_cot_compra = nr_cot_compra_p 
and exists ( 
	SELECT	1 
	from	cot_compra_forn_item b 
	where	a.nr_sequencia = b.nr_seq_cot_forn 
	and	b.vl_unitario_material > 0 
	and	Obter_Se_Cotacao_Preco_Igual(nr_sequencia) <> 0) 
order by 1;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_fornec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	count(*) 
	into STRICT	qt_contador_w 
	from	cot_compra_resumo 
	where	nr_seq_cot_forn = nr_seq_fornec_w 
	and	nr_item_cot_compra <> nr_item_cot_compra_p;
	 
	if (qt_contador_w >= qt_contador_ww) then 
		nr_seq_fornec_result_w	:= nr_seq_fornec_w;
		qt_contador_ww		:= qt_contador_w;
	end if;
	end;
end loop;
close C01;
 
return	nr_seq_fornec_result_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
