SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_fqd ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* 
Total de Quedas	'T' 
Total Pacientes Dia	'PD' 
Total Avaliação Queda 	'TA' 
*/
			 
ds_retorno_w	bigint;
nr_sequencia_w	bigint;
	

BEGIN 
 
 
if (ie_opcao_p = 'T') then 
	 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	count(*) 
		into STRICT	ds_retorno_w	 
		from	qua_evento_paciente b, 
				QUEDA_FICHA_OCORRENCIA e 
		where	e.nr_seq_evento = b.nr_sequencia 
		and		coalesce(b.dt_inativacao::text, '') = '' 
		and		trunc(b.dt_evento) = trunc(dt_inicio_p);
	else 
		select 	count(*) 
		into STRICT	ds_retorno_w	 
		from	qua_evento_paciente b, 
				QUEDA_FICHA_OCORRENCIA e 
		where	e.nr_seq_evento = b.nr_sequencia 
		and		coalesce(b.dt_inativacao::text, '') = '' 
		and	trunc(b.dt_evento) between trunc(dt_inicio_p) and trunc(dt_final_p);
 
	end if;
 
elsif (ie_opcao_p = 'PD') then 
 
	select	count(distinct nr_atendimento) 
	into STRICT	ds_retorno_w 
	from	resumo_atendimento_paciente_v 
	where	trunc(dt_entrada) <= trunc(dt_inicio_p) 
	and	coalesce(dt_alta,fim_dia(dt_inicio_p)) >= fim_dia(dt_inicio_p);
 
elsif (ie_opcao_p = 'TA') then 
 
	select	a.nr_sequencia 
	into STRICT	nr_sequencia_w 
	from	eif_escala a, 
			eif_escala_item b 
	where	a.ie_situacao = 'A' 
	and	b.nr_seq_escala = a.nr_sequencia 
	and	upper(ds_item) like upper('%agitado%') 
	and	upper(ds_escala) like upper('%queda%');
	 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	count(distinct nr_Atendimento) 
		into STRICT	ds_retorno_w 
		from	escala_eif 
		where	nr_seq_escala = nr_sequencia_w 
		and	coalesce(dt_inativacao::text, '') = '' 
		and	ie_situacao = 'A' 
		and	trunc(dt_avaliacao) = trunc(dt_inicio_p);
	else 
		select 	count(distinct nr_Atendimento) 
		into STRICT	ds_retorno_w 
		from	escala_eif 
		where	nr_seq_escala = nr_sequencia_w 
		and	coalesce(dt_inativacao::text, '') = '' 
		and	ie_situacao = 'A' 
		and	trunc(dt_avaliacao) between trunc(dt_inicio_p) and trunc(dt_final_p);
	end if;
 
	 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_fqd ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text) FROM PUBLIC;

