CREATE OR REPLACE FUNCTION obter_frete_item_rateado_nf ( nr_sequencia_p bigint, nr_item_nf_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_frete_w		nota_fiscal.vl_frete%type;
vl_frete_rateado_w		nota_fiscal.vl_frete%type;
nr_item_nf_w		nota_fiscal_item.nr_item_nf%type;
qt_itens_w		bigint;
vl_diferenca_w		double precision;


BEGIN
select	coalesce(a.vl_frete,0),
	(select count(b.nr_item_nf) from nota_fiscal_item b where b.nr_sequencia = a.nr_sequencia)
into STRICT	vl_frete_w,
	qt_itens_w
from	nota_fiscal a
where	a.nr_sequencia = nr_sequencia_p;
	
	if (vl_frete_w > 0) and (qt_itens_w > 1) then

		select 	 max(a.nr_item_nf)
		into STRICT	   nr_item_nf_w
		from 	   nota_fiscal_item a
		where	   nr_sequencia = nr_sequencia_p
		and	      a.vl_total_item_nf >=	0;

		vl_retorno_w := (trunc(vl_frete_w) / qt_itens_w);
			
		if nr_item_nf_w = nr_item_nf_p then		
			vl_retorno_w := vl_retorno_w + (vl_frete_w - trunc(vl_frete_w));
		end if;
	else
		vl_retorno_w := vl_frete_w;
	end if;

return	vl_retorno_w;

end;
$body$
