CREATE OR REPLACE FUNCTION obter_funcao_medico_convenio ( cd_convenio_p bigint, cd_funcao_p bigint, cd_especialidade_p bigint) RETURNS bigint AS $body$
DECLARE


cd_funcao_w				bigint;
cd_especialidade_w		bigint;


BEGIN
cd_especialidade_w	:= cd_especialidade_p;

if (coalesce(cd_especialidade_w::text, '') = '') then
