CREATE OR REPLACE FUNCTION obter_funcao_regra_grid ( cd_funcao_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_grid_p text) RETURNS varchar AS $body$
DECLARE


ds_ordem_grid_w	varchar(4000) := null;
nr_sequencia_w	bigint;
cd_estabelecimento_w smallint;
nm_grid_w	varchar(255);


C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_ordem_grid,
		coalesce(cd_estabelecimento,0)
	from	funcao_regra_grid
	where	cd_funcao	= cd_funcao_p
	and 	cd_perfil	= cd_perfil_p			
	and 	coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
	and	upper(nm_grid)	= nm_grid_w
	order by
		coalesce(cd_funcao,0),
		coalesce(cd_perfil,0),
		coalesce(cd_estabelecimento,0);


BEGIN		

nm_grid_w		:= upper(nm_grid_p);
cd_estabelecimento_w	:= coalesce(cd_estabelecimento_p,0);

OPEN C01;
LOOP
FETCH C01 INTO
	nr_sequencia_w,
	ds_ordem_grid_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN
		ds_ordem_grid_w := substr(ds_ordem_grid_w || ' #@' || nr_sequencia_w || ' #@' || cd_estabelecimento_w, 1, 4000);
	END;
END LOOP;
CLOSE C01;

return	ds_ordem_grid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
