SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_funcao_usuario_prescr ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nm_usuario_orig_w	varchar(15);



BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(nm_usuario_original)
	into STRICT	nm_usuario_orig_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	max(b.ds_valor_dominio)
	into STRICT	ds_retorno_w
	from	valor_dominio b,
		usuario a
	where	a.nm_usuario		= nm_usuario_orig_w
	and	b.cd_dominio		= 72
	and	a.ie_tipo_evolucao	= b.vl_dominio;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
