CREATE OR REPLACE FUNCTION obter_func_fisic_eortc_atu_md ( ie_dificuldade_esforco_p bigint, ie_dif_grande_caminhada_p bigint, ie_dif_curta_caminhada_p bigint, ie_cama_cadeira_dia_p bigint, ie_ajuda_alimentar_vestir_p bigint ) RETURNS bigint AS $body$
