CREATE OR REPLACE FUNCTION obter_gasto_pendente (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_prescr_w		double precision;
qt_devolucao_w		double precision;
ie_pendente_w		varchar(1) := 'N';
ie_param_3_w		varchar(1);
ie_param_327_w		varchar(255);

BEGIN

ie_param_3_w := obter_param_usuario(3111, 3, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_3_w);
ie_param_327_w := obter_param_usuario(3111, 327, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_327_w);

select	count(*)
into STRICT	qt_prescr_w
from	prescr_material_pendente_v
where	nr_atendimento = nr_atendimento_p
and		(coalesce(dt_liberacao, dt_liberacao_medico) IS NOT NULL AND (coalesce(dt_liberacao, dt_liberacao_medico))::text <> '')
and 	((coalesce(ie_agrupador,0) not in (ie_param_327_w) and ie_param_3_w = 'E') or (ie_param_3_w <> 'E'));

select	count(*)
into STRICT	qt_devolucao_w
from	devolucao_material_pac a,
	item_devolucao_material_pac i
where 	coalesce(a.dt_baixa_total::text, '') = ''
and	a.nr_devolucao   = i.nr_devolucao
and	coalesce(i.dt_recebimento::text, '') = ''
and	a.nr_atendimento = nr_atendimento_p;

if (qt_prescr_w <> 0) or (qt_devolucao_w <> 0) then
	ie_pendente_w	:= 'S';
end if;

return ie_pendente_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
