CREATE OR REPLACE FUNCTION obter_gerencia_grupo_desen ( nr_seq_grupo_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ds_gerencia_w		varchar(80);
nr_sequencia_w		bigint;


BEGIN
if (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
	begin
	select	b.ds_gerencia,
		b.nr_sequencia
	into STRICT	ds_gerencia_w,
		nr_sequencia_w
	from	gerencia_wheb b,
		grupo_desenvolvimento a
	where	a.nr_sequencia		= nr_seq_grupo_p
	and	a.nr_seq_gerencia	= b.nr_sequencia;
	end;
end if;

if (ie_cod_desc_p	= 'C') then
	ds_retorno_w	:= nr_sequencia_w;
else
	ds_retorno_w	:= ds_gerencia_w;
end if;

RETURN ds_retorno_w;

END;
$body$
