CREATE OR REPLACE FUNCTION obter_gerencia_grupo_sup ( nr_seq_grupo_sup_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_gerencia_sup_w			varchar(100);
ds_retorno_w			varchar(100);
nr_seq_gerencia_sup_w		bigint;


BEGIN

if (nr_seq_grupo_sup_p IS NOT NULL AND nr_seq_grupo_sup_p::text <> '') then

	select	a.nr_sequencia,
		a.ds_gerencia
	into STRICT	nr_seq_gerencia_sup_w,
		ds_gerencia_sup_w
	from	gerencia_wheb a,
		grupo_suporte b
	where	b.nr_seq_gerencia_sup	= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_grupo_sup_p;

	if (ie_opcao_p	= 'C') then
		ds_retorno_w	:= nr_seq_gerencia_sup_w;
	else
		ds_retorno_w	:= ds_gerencia_sup_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
