CREATE OR REPLACE FUNCTION obter_gerencia_usuario_wheb ( nm_usuario_p text) RETURNS bigint AS $body$
DECLARE



nr_seq_gerencia_w		bigint;


BEGIN

select	coalesce(max(y.nr_seq_gerencia), 0)
into STRICT	nr_seq_gerencia_w
from	grupo_desenvolvimento y,
		usuario_grupo_des x
where	y.nr_sequencia = x.nr_seq_grupo
and		upper(x.nm_usuario_grupo) = upper(nm_usuario_p)
and		coalesce(y.ie_situacao,'A') = 'A';

return nr_seq_gerencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
