SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_glicemia_prescr ( nr_atendimento_p bigint, nr_prescricao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_glicemia_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	if (ie_opcao_p = 'C') then /* cig */
		/* obter glicemia em andamento */

		select	coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_seq_glicemia_w
		from	atend_glicemia a
		where	a.nr_atendimento = nr_atendimento_p
		and	a.nr_prescricao = nr_prescricao_p
		and	((coalesce(a.nr_seq_prot_glic::text, '') = '') or (exists (SELECT 1 from pep_protocolo_glicemia b where b.nr_sequencia = a.nr_seq_prot_glic and b.ie_tipo = 'I')))
		and	a.ie_status_glic not in ('P','T','S');

		/* obter glicemia prescrita */

		if (nr_seq_glicemia_w = 0) then
			select	coalesce(max(a.nr_sequencia),0)
			into STRICT	nr_seq_glicemia_w
			from	atend_glicemia a
			where	a.nr_atendimento = nr_atendimento_p
			and	a.nr_prescricao = nr_prescricao_p
			and	((coalesce(a.nr_seq_prot_glic::text, '') = '') or (exists (SELECT 1 from pep_protocolo_glicemia b where b.nr_sequencia = a.nr_seq_prot_glic and b.ie_tipo = 'I')))
			and	a.ie_status_glic in ('P');
		end if;
	end if;
end if;

return nr_seq_glicemia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
