CREATE OR REPLACE FUNCTION obter_glosa_adm_tec ( nr_seq_hist_p bigint, nr_seq_guia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_glosa_w	double precision;


BEGIN

if (nr_seq_guia_p > 0) then

	select	coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_glosa_w
	from	lote_audit_hist_item a,
		motivo_glosa b,
		grupo_glosa c
	where	a.nr_seq_guia 	  = nr_seq_guia_p
	and	b.cd_motivo_glosa = a.cd_motivo_glosa
	and	c.nr_sequencia    = b.nr_seq_grupo
	and	c.ie_tipo_glosa	  = ie_opcao_p;
elsif (nr_seq_hist_p > 0) then

	select	coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_glosa_w
	from	lote_audit_hist x,
		lote_audit_hist_guia y,
		lote_audit_hist_item a,
		motivo_glosa b,
		grupo_glosa c
	where	x.nr_sequencia = y.nr_seq_lote_hist
	and	y.nr_sequencia = a.nr_seq_guia
	and	b.cd_motivo_glosa = a.cd_motivo_glosa
	and	c.nr_sequencia    = b.nr_seq_grupo
	and	x.nr_sequencia	  = nr_seq_hist_p
	and	c.ie_tipo_glosa	  = ie_opcao_p;
end if;

return	vl_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
