SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_glosa_ant_lote_item ( nr_seq_lote_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_seq_lote_w		bigint;
nr_analise_w		bigint;
nr_seq_propaci_w		bigint;
nr_seq_matpaci_w		bigint;


BEGIN

select	max(a.nr_analise),
	max(a.nr_seq_lote_audit),
	max(c.nr_seq_propaci),
	max(c.nr_seq_matpaci)
into STRICT	nr_analise_w,
	nr_seq_lote_w,
	nr_seq_propaci_w,
	nr_seq_matpaci_w
from	lote_audit_hist a,
	lote_audit_hist_guia b,
	lote_audit_hist_item c
where	c.nr_sequencia	= nr_seq_lote_item_p
and	c.nr_seq_guia	= b.nr_sequencia
and	b.nr_seq_lote_hist	= a.nr_sequencia;

/* valor glosado nas an√°lises anteriores */

select	coalesce(sum(c.vl_glosa),0)
into STRICT	vl_retorno_w
from	lote_audit_hist_item c,
	lote_audit_hist_guia b,
	lote_audit_hist a
where	a.nr_seq_lote_audit	= nr_seq_lote_w
and	a.nr_analise		< nr_analise_w
and	a.nr_sequencia		= b.nr_seq_lote_hist
and	b.nr_sequencia		= c.nr_seq_guia
and (c.nr_seq_propaci = nr_seq_propaci_w or c.nr_seq_matpaci = nr_seq_matpaci_w);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
