CREATE OR REPLACE FUNCTION obter_glosa_participante (nr_seq_procedimento_p bigint, nr_seq_material_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p

'G'	glosa
'A'	a maior

*/
vl_retorno_w	double precision	:= 0;
vl_glosa_w	double precision;
vl_amaior_w	double precision;


BEGIN

if (coalesce(nr_seq_procedimento_p,0) > 0) then
	select	coalesce(sum(a.VL_GLOSA),0),
		coalesce(sum(a.vl_amaior),0)
	into STRICT	vl_glosa_w,
		vl_amaior_w
	from	convenio_retorno_glosa a
	where (coalesce(a.cd_pessoa_fisica::text, '') = '' or a.cd_pessoa_fisica = cd_pessoa_fisica_p)
	and	NR_SEQ_PROPACI	= nr_seq_procedimento_p;
else
	select	coalesce(sum(a.VL_GLOSA),0),
		coalesce(sum(a.vl_amaior),0)
	into STRICT	vl_glosa_w,
		vl_amaior_w
	from	convenio_retorno_glosa a
	where (coalesce(a.cd_pessoa_fisica::text, '') = '' or a.cd_pessoa_fisica = cd_pessoa_fisica_p)
	and	NR_SEQ_MATPACI	= nr_seq_material_p;
end if;

if (ie_opcao_p	= 'A') then
	vl_retorno_w	:= vl_amaior_w;
elsif (ie_opcao_p	= 'G') then
	vl_retorno_w	:= vl_glosa_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
