CREATE OR REPLACE FUNCTION obter_glosa_procmat_rep ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_procedimento_w			double precision;
vl_material_w			double precision;
vl_retorno_w			double precision;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select	coalesce(sum(somente_positivo(coalesce(vl_repasse,0) - coalesce(vl_liberado,0))),0) vl
	into STRICT	vl_procedimento_w
	from 	procedimento_paciente c,
		procedimento_repasse b,
		titulo_pagar a
	where	a.nr_titulo		= nr_titulo_p
	and	a.nr_repasse_terceiro	= b.nr_repasse_terceiro
	and	b.nr_seq_procedimento	= c.nr_sequencia;

	select	coalesce(sum(somente_positivo(coalesce(vl_repasse,0) - coalesce(vl_liberado,0))),0) vl
	into STRICT	vl_material_w
	from	material_atend_paciente c,
		material_repasse b,
		titulo_pagar a
	where	a.nr_titulo		= nr_titulo_p
	and	a.nr_repasse_terceiro	= b.nr_repasse_terceiro
	and	b.nr_seq_material	= c.nr_sequencia;

	vl_retorno_w	:= coalesce(vl_procedimento_w,0) + coalesce(vl_material_w,0);
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_glosa_procmat_rep ( nr_titulo_p bigint) FROM PUBLIC;

