end;

else

begin

if (qt_pontos_p >= 8) then

	ds_dependencia_w 	:= wheb_mensagem_pck.get_texto(1112554); -- high dependence
elsif (qt_pontos_p >= 5 and qt_pontos_p <= 7) then

