SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_grupos_dieta_atend (nr_seq_atend_serv_p bigint) RETURNS varchar AS $body$
DECLARE


ds_grupo_w 	varchar(255);				
ds_retorno_w 	varchar(255);
ds_virgula_w	varchar(02);

c01 CURSOR FOR
	SELECT  x.nm_dieta
	FROM	nut_atend_serv_dia a,
		nut_atend_serv_dia_rep e,
		prescr_dieta b,
		dieta x
	WHERE 	a.nr_sequencia = e.nr_seq_serv_dia
	AND	e.nr_prescr_oral = b.nr_prescricao
	AND	x.cd_dieta = b.cd_dieta
	AND	a.nr_sequencia = nr_seq_atend_serv_p
	
union

	SELECT 	wheb_mensagem_pck.get_texto(1089759) --Jejum
	from 	nut_atend_serv_dia a,
		nut_atend_serv_dia_rep e,
		rep_jejum b
	where 	a.nr_sequencia = e.nr_seq_serv_dia
	and 	e.nr_prescr_jejum = b.nr_prescricao
	and 	a.nr_sequencia = nr_seq_atend_serv_p;


BEGIN
ds_virgula_w := '';

open	c01;
loop
fetch	c01 into
	ds_grupo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
begin
	ds_retorno_w := ds_retorno_w || ds_virgula_w || ds_grupo_w;
	ds_virgula_w		:= ', ';
end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_grupos_dieta_atend (nr_seq_atend_serv_p bigint) FROM PUBLIC;

