CREATE OR REPLACE FUNCTION obter_grupos_paciente ( nr_atendimento_p bigint, cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := '';
ds_grupo_w		varchar(60);
nr_seq_grupo_w		bigint;
ds_cor_w			varchar(15);
ds_grupo_cor_w		varchar(15);


C01 CURSOR FOR
	SELECT	nr_seq_grupo
	from	pac_grupo_atend
	where	nr_atendimento 	= nr_atendimento_p
	and	coalesce(dt_saida::text, '') = ''
	
union

	SELECT	nr_seq_grupo
	from	pac_grupo_atend
	where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and	coalesce(dt_saida::text, '') = '';


BEGIN

open c01;
	loop
	fetch c01 into
		nr_seq_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select 	ds_grupo
	into STRICT	ds_grupo_w
	from	pac_grupo
	where	nr_sequencia = nr_seq_grupo_w;

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_grupo_w;
	else
		ds_retorno_w := ds_retorno_w || ', ' || ds_grupo_w;
	end if;

	end;
	end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
