CREATE OR REPLACE FUNCTION obter_grupo_atend_operador (cd_operador_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_operador_p	bigint;
nr_seq_grupo_atend_p	bigint;
ds_retorno_w		bigint;


BEGIN

if (cd_operador_p IS NOT NULL AND cd_operador_p::text <> '') then
	begin

	select	coalesce(max(nr_sequencia), 0)
	into STRICT	nr_seq_operador_p
	from	pls_operador
	where	cd_pessoa_fisica = cd_operador_p
	and	ie_situacao = 'A';

	select	coalesce(max(nr_seq_grupo_atend), 0)
	into STRICT	nr_seq_grupo_atend_p
	from	pls_operador
	where	nr_sequencia = nr_seq_operador_p
	and	ie_situacao = 'A';

	ds_retorno_w	:= nr_seq_grupo_atend_p;
	end;

end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
