CREATE OR REPLACE FUNCTION obter_grupo_atraso_pepo ( nr_cirurgia_p bigint, nr_seq_evento_dif_p bigint) RETURNS bigint AS $body$
DECLARE


ie_tempo_atraso_w	bigint;
qt_tempo_limite_w	bigint;
nr_seq_evento_w		bigint;
dt_registro_inic_w	timestamp;
dt_registro_final_w	timestamp;
nr_seq_grupo_atraso_w	bigint;


BEGIN

select	max(qt_tempo_limite),
	max(nr_seq_evento),
	max(nr_seq_grupo_atraso)
into STRICT 	qt_tempo_limite_w,
	nr_seq_evento_w,
	nr_seq_grupo_atraso_w
from	tempo_cirurgico
where	nr_seq_evento_dif = nr_seq_evento_dif_p;

if (coalesce(qt_tempo_limite_w,0) > 0) then
	return nr_seq_grupo_atraso_w;
else
	return 0;
end if;

end;
$body$
