DECLARE


cd_grupo_atual_w		codificacao_grupo.nr_sequencia%type;


BEGIN

select	max(a.nr_sequencia)
into STRICT	cd_grupo_atual_w
from	codificacao_grupo a,
	codificacao_grupo_perfil b
where	a.nr_sequencia 	= b.nr_seq_grupo
