CREATE OR REPLACE FUNCTION obter_grupo_ausencia_usuario ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);


BEGIN
select	max(g.ds_grupo)
into STRICT	ds_retorno_w
from	escala_diaria d,
	escala_grupo g,
	escala e,
	usuario u
where	u.cd_pessoa_fisica	= d.cd_pessoa_fisica
and	g.nr_sequencia = e.nr_seq_grupo
and	e.nr_sequencia = d.nr_seq_escala
and	u.ie_situacao = 'A'
and	u.nm_usuario	= nm_usuario_p;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
