CREATE OR REPLACE FUNCTION obter_grupo_cobr_cobranca (nr_seq_cobranca_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_cobrador_w	bigint;
nr_seq_grupo_w		bigint	:= null;


BEGIN
if (nr_seq_cobranca_p IS NOT NULL AND nr_seq_cobranca_p::text <> '') then
	select 	a.nr_seq_cobrador
	into STRICT	nr_seq_cobrador_w
	from    cobranca a
	where   a.nr_sequencia = nr_seq_cobranca_p;

	select	max(a.nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	grupo_cobranca_membro a
	where	a.nr_seq_cobrador = nr_seq_cobrador_w;
end if;

return	nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
