CREATE OR REPLACE FUNCTION obter_grupo_compras_material ( cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
cd_grupo_material_ww			smallint;
cd_subgrupo_material_ww			smallint;
cd_material_w				integer;
cd_classe_material_ww			integer;
nr_seq_grupo_w				bigint;

c01 CURSOR FOR
SELECT	distinct
	c.nr_sequencia nr_seq_grupo,
	a.cd_material,
	a.cd_classe_material,
	a.cd_subgrupo_material,
	a.cd_grupo_material
from	sup_estrut_compras a,
	sup_subgrupo_compra b,
	sup_grupo_compra c
where	a.nr_seq_subgrupo_compra = b.nr_sequencia
and	b.nr_seq_grupo_compra = c.nr_sequencia
and	coalesce(a.cd_grupo_material, cd_grupo_material_w)     		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(a.cd_material, cd_material_p)			= cd_material_p or cd_material_p = 0)
and	a.ie_liberado = 'S'
and	coalesce(c.ie_situacao,'A') = 'A'
and	coalesce(b.ie_situacao,'A') = 'A'
order by a.cd_material desc,
	a.cd_classe_material desc,
	a.cd_subgrupo_material desc,
	a.cd_grupo_material desc;


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;


open C01;
loop
fetch C01 into
	nr_seq_grupo_w,
	cd_material_w,
	cd_classe_material_ww,
	cd_subgrupo_material_ww,
	cd_grupo_material_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_grupo_w := nr_seq_grupo_w;
	end;
end loop;
close C01;

return	nr_seq_grupo_w;

end;
$body$
