CREATE OR REPLACE FUNCTION obter_grupo_consulta_frase ( ds_filtro_p text, ie_tipo_consulta_p text) RETURNS varchar AS $body$
DECLARE

nr_seq_grupo_w		bigint;
nr_seq_superior_w 	bigint;
ds_retorno_w 		varchar(4000);

C01 CURSOR FOR
	SELECT distinct(nr_seq_grupo)
	from   protocolo_descricao
	where  (nr_seq_grupo IS NOT NULL AND nr_seq_grupo::text <> '')
	and    (((ie_tipo_consulta_p = 'DS') and upper(ds_frase) like upper('%' || ds_filtro_p || '%')) or
	        ((ie_tipo_consulta_p = 'NR') and nr_sequencia like '%' || ds_filtro_p || '%' ) or
	        ((ie_tipo_consulta_p = 'CD') and upper(cd_interno) like upper('%' || ds_filtro_p || '%')));


BEGIN

ds_retorno_w := '';

if (ds_filtro_p IS NOT NULL AND ds_filtro_p::text <> '') and (ie_tipo_consulta_p IS NOT NULL AND ie_tipo_consulta_p::text <> '') then

	if (ie_tipo_consulta_p <> 'GR') then
	    open c01;
	    loop
	    fetch c01 into
	        nr_seq_grupo_w;
	    EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select coalesce(max(nr_seq_superior),0)
		into STRICT nr_seq_superior_w
		from protocolo_grupo_descricao
		where nr_sequencia = nr_seq_grupo_w;

		ds_retorno_w := substr(ds_retorno_w || to_char(nr_seq_grupo_w),1,3999) || ',';

		if (nr_seq_superior_w <> 0) then

			while(nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') loop
			   begin
			   ds_retorno_w := substr(ds_retorno_w || to_char(nr_seq_superior_w),1,3999) || ',';

			   select  max(nr_seq_superior)
			   into STRICT nr_seq_superior_w
			   from protocolo_grupo_descricao
			   where nr_sequencia = nr_seq_superior_w;

			   end;
			end loop;
		end if;

		end;
	    end loop;
	    close c01;
	else
		nr_seq_superior_w :=  ds_filtro_p;

		while(nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') loop
		   begin
		   ds_retorno_w := substr(ds_retorno_w || to_char(nr_seq_superior_w),1,3999) || ',';

		   select  max(nr_seq_superior)
		   into STRICT nr_seq_superior_w
		   from protocolo_grupo_descricao
		   where nr_sequencia = nr_seq_superior_w;

		   end;
		end loop;
	end if;

    ds_retorno_w := substr(ds_retorno_w,1,Length(ds_retorno_w)-1);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
