CREATE OR REPLACE FUNCTION obter_grupo_conta_proc (nr_interno_conta_p bigint, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE

                                         
                                         
cd_procedimento_w	varchar(15);
ds_retorno_w		varchar(4000);

C01 CURSOR FOR                                  
		SELECT 	b.cd_procedimento                           
		from	conta_paciente a,                             
			procedimento_paciente b,                           
			procedimento c                                
		where	a.nr_interno_conta = b.nr_interno_conta                 
		and	b.cd_procedimento = c.cd_procedimento                                           
		and 	c.ie_classificacao = 1 
		and	((b.cd_especialidade = cd_especialidade_p) or (cd_especialidade_p = 0)) 
		and	a.nr_interno_conta = nr_interno_conta_p                  
		group by b.cd_procedimento                           
		order by cd_procedimento;


BEGIN                                      
                                         
ds_retorno_w := '';
                                         
open C01;
loop                                       
fetch C01 into                                  
	cd_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin                                      
	ds_retorno_w := ds_retorno_w||cd_procedimento_w;
	end;
end loop;
close C01;
                                         
return	ds_retorno_w;
                                         
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
