CREATE OR REPLACE FUNCTION obter_grupo_desenv_resp_funcao (cd_funcao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_desenv_w	bigint;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') then
	begin
	select	min(g.nr_sequencia)
	into STRICT	nr_seq_grupo_desenv_w
	from	grupo_desenvolvimento g,
		funcao_grupo_des f
	where	g.nr_sequencia = f.nr_seq_grupo
	and	f.cd_funcao = cd_funcao_p
	and	coalesce(g.ie_situacao,'A') = 'A';
	end;
end if;
return nr_seq_grupo_desenv_w;
end;
$body$
LANGUAGE PLPGSQL
