CREATE OR REPLACE FUNCTION obter_grupo_des_os_data (nr_seq_ordem_servico_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_des_w	grupo_desenvolvimento.nr_sequencia%type := null;


BEGIN

select	max(nr_seq_grupo_des)
into STRICT	nr_seq_grupo_des_w
from	man_ordem_log_grupo_des a
where	nr_seq_ordem_serv = nr_seq_ordem_servico_p
and	dt_atualizacao = (	SELECT	max(b.dt_atualizacao)
				from	man_ordem_log_grupo_des b
				where	b.nr_seq_ordem_serv = nr_seq_ordem_servico_p
				and	b.dt_atualizacao < dt_referencia_p);

if (coalesce(nr_seq_grupo_des_w::text, '') = '') then

	select	max(nr_seq_grupo_des)
	into STRICT	nr_seq_grupo_des_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_servico_p;

end if;

return	nr_seq_grupo_des_w;

end;
