CREATE OR REPLACE FUNCTION obter_grupo_des_usuario (nm_usuario_p usuario.nm_usuario%type) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_w	gerencia_wheb_grupo.nr_sequencia%type;


BEGIN

select	max(ugd.nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from	usuario_grupo_des ugd,
	usuario usu
where	ugd.nm_usuario_grupo = usu.nm_usuario
and	usu.ie_situacao = 'A'
and	ugd.ie_emprestimo = 'N'
and	trunc(clock_timestamp()) between trunc(ugd.dt_inicio_vigencia) and trunc(fim_dia(ugd.dt_fim_vigencia))
and	ugd.nm_usuario_grupo = nm_usuario_p;

return	nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
